import 'dart:convert';
import 'dart:io';
import 'dart:typed_data';
import 'package:flutter_screenutil/flutter_screenutil.dart';

import 'package:flutter/cupertino.dart';
import 'package:flutter/material.dart';
import 'package:flutter/services.dart';
import 'package:guided/constants/asset_path.dart';
import 'package:image_picker/image_picker.dart';
import 'package:modal_bottom_sheet/modal_bottom_sheet.dart';

import '../../../../../constants/app_colors.dart';
import '../../../profile_widgets.dart';

class UpdateProfileScreen extends StatefulWidget {
  const UpdateProfileScreen({Key? key}) : super(key: key);

  @override
  State<UpdateProfileScreen> createState() => _UpdateProfileScreenState();
}

class _UpdateProfileScreenState extends State<UpdateProfileScreen> {
  File? profileImg;

  String dp = '';
  @override
  Widget build(BuildContext context) {
    return Scaffold(
        resizeToAvoidBottomInset: true,
        backgroundColor: Colors.white,
        body: SafeArea(
          child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                backButton(context),
              const Padding(
                padding: EdgeInsets.only(left: 32,top: 19),
                child: Text(
                  "Edit Profile",
                  style: TextStyle(
                    fontSize: 24,
                    height: 1.2,
                    fontFamily: 'Gilroy-Light',
                    fontWeight: FontWeight.w600),
                ),
              ),
                getImage()],
            )
    ));
  }


  Center getImage() {

    return Center(
        child:Container(
          margin: EdgeInsets.only(top: 70),
            child:Column(
              children: [
                Stack(
                  fit: StackFit.loose, // Just Changed this line
                  alignment: Alignment.topRight, // Just changed this line
                  children: [
                    Align(
                      alignment: Alignment.topCenter,
                      child:Container(
                        decoration: BoxDecoration(
                          color: Colors.white,
                          shape: BoxShape.circle,
                          boxShadow: <BoxShadow>[
                            BoxShadow(
                                blurRadius: 2, color: AppColors.galleryWhite, spreadRadius: 2)
                          ],
                        ),
                        child: CircleAvatar(
                            radius: 110,
                            backgroundColor: Colors.white,
                            child:CircleAvatar(
                              radius: 100.0,
                              backgroundColor:  Colors.white,
                              backgroundImage: (profileImg==null?MemoryImage(base64.decode(dp)):FileImage(profileImg!)) as ImageProvider,
                            )
                        ),
                      ),
                    ),
                    Positioned(
                        top:1,
                        left: 225,
                        child: RawMaterialButton(
                          onPressed: () {
                            showMaterialModalBottomSheet(
                                expand: false,
                                context: context,
                                backgroundColor: Colors.transparent,
                                builder: (BuildContext context) => SafeArea(
                                    top: false,
                                    child: Container(
                                      color: Colors.white,
                                      child: Column(
                                        mainAxisSize: MainAxisSize.min,
                                        children: <Widget>[
                                          ListTile(
                                            leading: const Icon(Icons.photo_camera),
                                            title: const Text('Camera'),
                                            onTap: () async {
                                              try {
                                                final XFile? profileImg = await ImagePicker()
                                                    .pickImage(
                                                    source: ImageSource.camera,
                                                    maxHeight: 480,
                                                    maxWidth: 640,
                                                    imageQuality: 25);

                                                if (profileImg == null) {
                                                  return;
                                                }

                                                final File imageTemporary = File(profileImg.path);

                                                setState(() {
                                                  this.profileImg = imageTemporary;
                                                });
                                              } on PlatformException catch (e) {
                                                print('Failed to pick image: $e');
                                              }
                                              Navigator.of(context).pop();
                                            },
                                          ),
                                          ListTile(
                                            leading: const Icon(Icons.photo_album),
                                            title: const Text('Gallery'),
                                            onTap: () async {
                                              try {
                                                final XFile? profileImg = await ImagePicker()
                                                    .pickImage(
                                                    source: ImageSource.gallery,
                                                    maxHeight: 480,
                                                    maxWidth: 640,
                                                    imageQuality: 25);

                                                if (profileImg == null) {
                                                  return;
                                                }

                                                final File imageTemporary = File(profileImg.path);
                                                setState(() {
                                                  this.profileImg = imageTemporary;
                                                });
                                              } on PlatformException catch (e) {
                                                print('Failed to pick image: $e');
                                              }
                                              Navigator.of(context).pop();
                                            },
                                          ),
                                        ],
                                      ),
                                    )));

                          },
                          elevation: 2.0,
                          fillColor: Colors.white,
                          child: Icon(Icons.edit),
                          padding: EdgeInsets.all(15.0),
                          shape: CircleBorder(),
                        )),
                  ],
                ),
                
                Align(
                  alignment: FractionalOffset.bottomCenter,
                  child:
                  SizedBox(
                    width: double.infinity,
                    height: 70.h,
                    child: ElevatedButton(
                        onPressed: () {

                        },
                        style: ButtonStyle(
                            shape: MaterialStateProperty.all<
                                RoundedRectangleBorder>(
                              RoundedRectangleBorder(
                                borderRadius: BorderRadius.circular(50.r),
                              ),
                            )),
                        child: Text(
                          "Next",
                          style: TextStyle(
                              fontSize: 16.sp,
                              fontFamily: 'source-sans-pro',
                              fontWeight: FontWeight.w600),
                        )),
                  ),
                ),
                SizedBox(height: 40.h,),

              ],
            )

        )

    );
  }

  Widget profileWidget(BuildContext context) {
    return GestureDetector(
      onTap: () =>
          showMaterialModalBottomSheet(
          expand: false,
          context: context,
          backgroundColor: Colors.transparent,
          builder: (BuildContext context) => SafeArea(
              top: false,
              child: Container(
                color: Colors.white,
                child: Column(
                  mainAxisSize: MainAxisSize.min,
                  children: <Widget>[
                    ListTile(
                      leading: const Icon(Icons.photo_camera),
                      title: const Text('Camera'),
                      onTap: () async {
                        try {
                          final XFile? profileImg = await ImagePicker()
                              .pickImage(
                              source: ImageSource.camera,
                              maxHeight: 480,
                              maxWidth: 640,
                              imageQuality: 25);

                          if (profileImg == null) {
                            return;
                          }

                          final File imageTemporary = File(profileImg.path);

                          setState(() {
                            this.profileImg = imageTemporary;
                          });
                        } on PlatformException catch (e) {
                          print('Failed to pick image: $e');
                        }
                        Navigator.of(context).pop();
                      },
                    ),
                    ListTile(
                      leading: const Icon(Icons.photo_album),
                      title: const Text('Gallery'),
                      onTap: () async {
                        try {
                          final XFile? profileImg = await ImagePicker()
                              .pickImage(
                              source: ImageSource.gallery,
                              maxHeight: 480,
                              maxWidth: 640,
                              imageQuality: 25);

                          if (profileImg == null) {
                            return;
                          }

                          final File imageTemporary = File(profileImg.path);
                          setState(() {
                            this.profileImg = imageTemporary;
                          });
                        } on PlatformException catch (e) {
                          print('Failed to pick image: $e');
                        }
                        Navigator.of(context).pop();
                      },
                    ),
                  ],
                ),
              ))),
      child: profileImg != null
          ? Center(
        child: Stack(
          children: <Widget>[
            ClipOval(
              child: Container(
                padding: const EdgeInsets.all(10),
                color: Colors.blue[200],
                child: ClipOval(
                  child: Container(
                    color: Colors.white,
                    child: ClipRRect(
                        borderRadius: BorderRadius.circular(300.r),
                        child: Image.file(
                          profileImg!,
                          width: 130,
                          height: 130,
                          fit: BoxFit.fitHeight,
                        )),
                  ),
                ),
              ),
            ),
            Positioned(
                bottom: 0,
                right: 0,
                child: Container(
                  decoration: BoxDecoration(
                      borderRadius: BorderRadius.circular(30.r),
                      color: AppColors.tabTextNotSelected),
                  child: const Padding(
                    padding: EdgeInsets.all(8),
                    child: Icon(
                      Icons.camera_alt,
                      color: Colors.white,
                    ),
                  ),
                ))
          ],
        ),
      )
          : Center(
        child: Stack(
          children: <Widget>[
            ClipOval(
              child: Container(
                padding: const EdgeInsets.all(10),
                color: Colors.blue[200],
                child: ClipOval(
                  child: Container(
                    color: Colors.white,
                    child: ClipRRect(
                        borderRadius: BorderRadius.circular(300.r),
                        child: Image.asset(
                          AssetsPath.forThePlanet,
                          height: 130,
                          width: 130,
                          fit: BoxFit.fitHeight,
                        )),
                  ),
                ),
              ),
            ),
            Positioned(
                bottom: 0,
                right: 0,
                child: Container(
                  decoration: BoxDecoration(
                      borderRadius: BorderRadius.circular(30.r),),
                      // color: AppColors.brightCerulean),
                  child: const Padding(
                    padding: EdgeInsets.all(8),
                    child: Icon(
                      Icons.camera_alt,
                      color: Colors.white,
                    ),
                  ),
                ))
          ],
        ),
      ),
    );
  }


}
